mapper.insert:
        insert into t_activity(user_id, act, id, gmt_create)
        values (#{userId}, #{act}, #{id}, #{gmtCreate})
#         on duplicate key update gmt_create=#{gmtCreate};

mapper.delete:
delete
from t_activity
where user_id = #{userId}
  and act = #{act}
  and id = #{id};

mapper.select:
select user_id,gmt_create,act,id
where user_id = #{userId}
ORDER BY gmt_create DESC
LIMIT #{offset},#{limit};
