mapper.select:
SELECT uid,UNIX_TIMESTAMP(signup_time) signup_time,username,password,authority,nickname,sex
FROM t_user
WHERE username = #{username};

SELECT uid,nickname
FROM t_user
WHERE uid = #{uid};

mapper.update:
UPDATE t_user
SET nickname=#{nickname},
    sex=#{sex}
WHERE uid = #{uid};

mapper.select:
SELECT support_uid uid
FROM t_support
WHERE uid = #{uid};

SELECT uid
FROM t_support
WHERE support_uid = #{support_uid};
