mapper.select:
SELECT uid,password
FROM t_user
WHERE username = #{username};

mapper.update:
UPDATE t_user
SET nickname=#{nickname},
    sex=#{sex},
    intro=#{intro}
WHERE uid = #{uid};

mapper.select:
SELECT support_uid uid
FROM t_support
WHERE uid = #{uid};

SELECT uid
FROM t_support
WHERE support_uid = #{support_uid};
