mapper.select:
SELECT user_Id,password
FROM t_user
WHERE username = #{username};

mapper.update:
UPDATE t_user
SET nickname=#{nickname},
    sex=#{sex},
    intro=#{intro}
WHERE user_Id = #{userId};

mapper.delete:
delete
from t_follow
where user_Id = #{userId}
  and follower_id = #{followerId};

mapper.select:
select user_Id
from t_follow
where follower_id = #{followerId}

select follower_id
from t_follow
where user_Id = #{userId}
