mapper.select:
SELECT u_id,password
FROM t_user
WHERE username = #{username};

mapper.update:
UPDATE t_user
SET nickname=#{nickname},
    sex=#{sex},
    intro=#{intro}
WHERE u_id = #{uId};

mapper.delete:
delete
from t_follow
where u_id = #{uId}
  and follower_id = #{followerId};

mapper.select:
select u_id
from t_follow
where follower_id = #{followerId}

select follower_id
from t_follow
where u_id = #{uId}
