mapper.select:
SELECT user_Id, password
FROM t_user
WHERE username = #{username};

mapper.update:
UPDATE t_user
SET nickname=#{nickname},
    sex=#{sex},
    intro=#{intro},
    email=#{email}
WHERE user_Id = #{userId};

mapper.select:
select password
from t_user
WHERE user_Id = #{userId}

mapper.update:
UPDATE t_user
SET password=#{newPassword}
WHERE user_Id = #{userId}

mapper.select:
SELECT answer_id
FROM t_answer
WHERE user_id = #{userId}
  AND anonymous = false;

SELECT question_id
FROM t_question
WHERE user_id = #{userId}
  AND anonymous = false;
